    //  <constructor-arg />
      parseConstructorArgElements(ele, bd);
      //  <property />
      parsePropertyElements(ele, bd);
      //  <qualifier />
      parseQualifierElements(ele, bd);

      bd.setResource(this.readerContext.getResource());
      bd.setSource(extractSource(ele));

